<body>
    <!-- Content -->

    <div class="authentication-wrapper authentication-cover">
        <div class="authentication-inner row m-0">
            <!-- /Left Text -->
            <div class="d-none d-lg-flex col-lg-7 col-xl-8 align-items-center p-5">
                <div class="w-100 d-flex justify-content-center">
                    <img src="/assets/img/illustrations/girl-with-laptop-light.png" class="img-fluid" alt="Login image"
                        width="700" data-app-dark-img="illustrations/girl-with-laptop-dark.png"
                        data-app-light-img="illustrations/girl-with-laptop-light.png" />
                </div>
            </div>
            <!-- /Left Text -->

            <!-- Register -->
            <div class="d-flex col-12 col-lg-5 col-xl-4 align-items-center authentication-bg p-sm-5 p-4">
                <div class="w-px-400 mx-auto">
                    <!-- Logo -->
                    <div class="app-brand mb-5">
                        <a href="/register" class="app-brand-link gap-2">
                            <img width="60px"
                                src="data:image/png;base64,"
                                alt="Sipri Unsil Informatika">
                            <span class="app-brand-text demo text-body fw-bolder">Sipri Unsil</span>
                        </a>
                    </div>
                    <!-- /Logo -->
                    <h4 class="mb-2">Mulai melakukan peminjaman dan kelola sarana dan prasarana dengan Sipri Unsil! ðŸš€
                    </h4>
                    <p class="mb-4">Ajukan peminjaman ruangan, kelas maupun lab dengan mudah!</p>

                    <form id="form" class="mb-3" action="index.html" method="POST">
                        <div class="mb-3">
                            <label for="nama" class="form-label">Nama</label>
                            <input type="text" class="form-control" id="nama" name="nama" placeholder="Masukkan Nama"
                                autofocus />
                            <div class="invalid-feedback"></div>

                        </div>
                        <div class="mb-3">
                            <label for="username" class="form-label">Username</label>
                            <input type="text" class="form-control" id="username" name="username"
                                placeholder="Masukkan Username" autofocus />
                            <div class="invalid-feedback"></div>
                        </div>
                        <div class="mb-3">
                            <label for="no_telepon" class="form-label">Nomor Telepon</label>
                            <input type="text" class="form-control" id="no_telepon" name="no_telepon"
                                placeholder="Masukkan No. Telepon" autofocus />
                                <div class="invalid-feedback"></div>
                        </div>
                        <div class="mb-3 form-password-toggle">
                            <label class="form-label" for="password">Password</label>
                            <div class="input-group input-group-merge">
                                <input type="password" id="password" class="form-control" name="password"
                                    placeholder="&#xb7;&#xb7;&#xb7;&#xb7;&#xb7;&#xb7;&#xb7;&#xb7;&#xb7;&#xb7;&#xb7;&#xb7;"
                                    aria-describedby="password" />
                                <span class="input-group-text cursor-pointer"><i class="bx bx-hide"></i></span>
                            </div>
                            <p id="invalid-password" class="mt-2 mb-0" style="color: #ff3c1c; font-size: 0.875em;"></p>
                        </div>

                        <button class="btn btn-primary d-grid w-100" id="submit">Daftar</button>
                    </form>

                    <p class="text-center">
                        <span>Sudah memiliki akun Sipri Unsil?</span>
                        <a href="/login">
                            <span>Login</span>
                        </a>
                    </p>

                </div>
            </div>
            <!-- /Register -->
        </div>
    </div>

    <!-- / Content -->

    <!-- Core JS -->
    <!-- Core JS -->
    <!-- build:js assets/vendor/js/core.js -->
    <script src="/assets/vendor/libs/jquery/jquery.js"></script>
    <script src="/assets/vendor/js/bootstrap.js"></script>

    <script src="/assets/vendor/js/menu.js"></script>
    <!-- endbuild -->

    <!-- Vendors JS -->
    <script src="/assets/vendor/libs/blockui/jquery.blockui.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/simple-notify/dist/simple-notify.min.js"></script>

    <!-- Main JS -->
    <script src="/assets/js/main.js"></script>

    <!-- Page JS -->
    <script src="/assets/js/pages-auth.js"></script>

    <script>
    function showNotify({
        status = 'success',
        title = 'Notify Title',
        text = 'Notify text lorem ipsum',
        effect = 'slide',
        speed = 300,
        customClass = '',
        customIcon = '',
        showIcon = true,
        showCloseButton = true,
        autoclose = true,
        autotimeout = 3000,
        notificationsGap = null,
        notificationsPadding = null,
        type = 'outline',
        position = 'x-center',
        customWrapper = '',
    } = {}) {
        new Notify({
            status,
            title,
            text,
            effect,
            speed,
            customClass,
            customIcon,
            showIcon,
            showCloseButton,
            autoclose,
            autotimeout,
            notificationsGap,
            notificationsPadding,
            type,
            position,
            customWrapper,
        });
    }

    $(document).ready(function() {
        $("#closeAlert").click(function() {
            $("#myAlert").fadeOut();
        });

        $("#form input").on("input", function() {
            if ($(this).attr("id") === "remember-me") {
                return;
            }

            $(this).removeClass("is-invalid");
            if ($(this).attr("id") === "password") {
                $("#invalid-password").text("");
            } else {
                $(this).next().text("");
            }
        });

        $('#form').submit(function(e) {
            e.preventDefault();
            $.ajax({
                type: "POST",
                url: "/register/save",
                data: $(this).serialize(),
                dataType: "JSON",
                beforeSend: function() {
                    $("#submit").html(
                        '<span class="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span> Daftar'
                    ).addClass("disabled");
                },
                complete: function() {
                    $("#submit").html('Daftar').removeClass("disabled");
                },
                success: function(response) {
                    if (response.status == true) {
                          showNotify({
                            status: 'success',
                            title: 'Berhasil',
                            text: response.message,
                            type: 'fill',
                            autoclose: true,
                            autotimeout: 2000
                        });

                        setTimeout(function() {
                            window.location.href = '/login';
                        }, 2200);
                    } else if (response.status_form == false) {
                        $.each(response.errors, function(key, value) {
                            $('[name="' + key + '"]').addClass('is-invalid');
                            if (key == "password") {
                                $('#invalid-password').text(value);
                            } else {
                                $('[name="' + key + '"]').next().text(value);
                            }
                        });
                    } else if (response.status == false) {
                        showNotify({
                            status: 'warning',
                            title: 'Gagal',
                            text: response.message,
                            type: 'fill'
                        });
                    }
                },
                error: function(jqXHR, textStatus, errorThrown, exception) {
                    var msg = '';
                    if (jqXHR.status === 0) {
                        msg = 'Not connect.\n Verify Network.';
                    } else if (jqXHR.status == 404) {
                        msg = 'Requested page not found. [404]';
                    } else if (jqXHR.status == 500) {
                        msg = 'Internal Server Error [500].';
                    } else if (exception === 'parsererror') {
                        msg = 'Requested JSON parse failed.';
                    } else if (exception === 'timeout') {
                        msg = 'Time out error.';
                    } else if (exception === 'abort') {
                        msg = 'Ajax request aborted.';
                    } else {
                        msg = 'Uncaught Error.\n' + jqXHR.responseText;
                    }
                    $("#submit").html('Daftar').removeClass("disabled");
                    showNotify({
                        status: 'error',
                        title: 'Error',
                        text: msg,
                        type: 'fill'
                    });
                }
            });
        });

        $('.slideshow-container').slick({
            autoplay: true,
            autoplaySpeed: 10000,
            dots: true,
            arrows: true,
            infinite: true,
            speed: 500,
            fade: true
        });
    });
    </script>
</body>

</html>